INSERT INTO "vulnerable" VALUES ('708', '7de7f37a-6360-4656-8fc3-8e1d947a362b', 'OpenSSLX509_NAME_oneline函数拒绝服务漏洞(CVE-2016-2176)', NULL, '厂商补丁:OpenSSLProject---------------OpenSSLProject已经为此发布了一个安全公告（20160503）以及相应补丁:OpenSSL1.0.2用户升级到1.0.2hOpenSSL1.0.1用户升级到1.0.1t20160503：OpenSSLSecurityAdvisory[3rdMay2016]链接：https://www.openssl.org/news/secadv/20160503.txt', '中危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。OpenSSL某些版本中,crypto/x509/x509_obj.c中X509_NAME_oneline函数存在安全漏洞,通过构造的EBCDICASN.1数据,远程攻击者可获取进程内存的敏感信息或造成拒绝服务。<*来源：GuidoVranken链接：https://www.openssl.org/news/secadv/20160503.txt*>', '', 'CVE-2016-2176', '2018-04-03 13:52:14.132000');
INSERT INTO "vulnerable" VALUES ('709', '6211965f-ed02-40df-9b5f-6d1254f6a353', 'OpenSSLEVP_EncodeUpdate()函数整数溢出漏洞(CVE-2016-2106)', NULL, '厂商补丁:OpenSSLProject---------------OpenSSLProject已经为此发布了一个安全公告（20160503）以及相应补丁:OpenSSL1.0.2用户升级到1.0.2hOpenSSL1.0.1用户升级到1.0.1t20160503：OpenSSLSecurityAdvisory[3rdMay2016]链接：https://www.openssl.org/news/secadv/20160503.txt', '中危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。OpenSSL某些版本中,crypto/evp/encode.c里用于二进制数据Base64编码的EVP_EncodeUpdate()函数存在整数溢出漏洞,通过大量的二进制数据,远程攻击者可造成拒绝服务(堆内存破坏)。', '', 'CVE-2016-2106', '2018-04-03 13:52:14.239000');
