INSERT INTO "vulnerable" VALUES ('831', 'e65491f5-0e95-42e9-9134-8a5980612a3f', 'OracleDatabaseServer远程安全漏洞(CVE-2017-3486)', NULL, '厂商补丁：Oracle------Oracle已经为此发布了一个安全公告（cpuapr2017-3236618）以及相应补丁:cpuapr2017-3236618：OracleCriticalPatchUpdateAdvisory-April2017链接：http://www.oracle.com/technetwork/security-advisory/cpuapr2017-3236618.html', '低危', 'OracleDatabaseServer是一个对象一关系数据库管理系统。它提供开放的、全面的、和集成的信息管理方法。OracleDatabaseServer11.2.0.4,12.1.0.2版本中，SQL*Plus组件存在安全漏洞，远程用户利用此漏洞可影响可用性、机密性、完整性。<*来源：Oracle链接：http://www.oracle.com/technetwork/security-advisory/cpuapr2017-3236618.html*>', '', 'CVE-2017-3486', '2018-04-03 13:52:34.717000');
