INSERT INTO "vulnerable" VALUES ('833', '228710d6-082f-40bb-a82f-ef42763e4c9a', 'OracleDatabaseServer远程安全漏洞(CVE-2016-3562)', NULL, '厂商补丁：Oracle------Oracle已经为此发布了一个安全公告（cpuoct2016-2881722）以及相应补丁:cpuoct2016-2881722：OracleCriticalPatchUpdateAdvisory-October2016链接：http://www.oracle.com/technetwork/security-advisory/cpuoct2016-2881722.html', '低危', 'OracleDatabaseServer是一个对象一关系数据库管理系统。它提供开放的、全面的、和集成的信息管理方法。OracleDatabaseServer11.2.0.4,12.1.0.2版本中，RDBMSSecurity/SQL*Plus组件存在安全漏洞，远程用户利用此漏洞可影响机密性。<*来源：Oracle链接：http://www.oracle.com/technetwork/security-advisory/cpuoct2016-2881722.html*>', '', 'CVE-2016-3562', '2018-04-03 13:52:34.935000');
