INSERT INTO "vulnerable" VALUES ('891', 'a13dbaad-e684-43b3-bbbf-aece7ce95db3', 'IBMDB2远程拒绝服务漏洞(CVE-2009-0172)', NULL, '厂商补丁:目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接：http://www-01.ibm.com/support/docview.wss?uid=swg27007053http://www-01.ibm.com/support/docview.wss?uid=swg21363936http://www-01.ibm.com/support/docview.wss?uid=swg21372517', '中危', 'IBMDB2修复补丁17a之前的8版本，修复补丁6a之前的9.1版本以及修复补丁3a之前的9.5版本中存在未明漏洞。远程攻击者可以借助一个特制的CONNECT数据流，引起拒绝服务攻击(死循环)。', '', 'CVE-2009-0172', '2018-04-03 13:52:42.756000');
