INSERT INTO "vulnerable" VALUES ('1640', '62c25b1d-5c55-4ed9-b02e-9affe0d81224', 'DigiumAsteriskOpenSource安全漏洞(CVE-2016-9938)', NULL, '厂商补丁:目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://downloads.asterisk.org/pub/security/AST-2016-009.html', '中危', 'DigiumAsteriskOpenSource是美国Digium公司的一套开源的电话交换机（PBX）系统软件。该软件支持语音信箱、多方语音会议、交互式语音应答(IVR)等。DigiumAsteriskOpenSource中存在安全漏洞。攻击者可利用该漏洞执行未授权的操作。', '', 'CVE-2016-9938', '2018-04-03 13:54:12.029000');
INSERT INTO "vulnerable" VALUES ('1641', '082f6b6f-ad9c-4db0-8bf2-e230a2c2a025', 'DigiumAsteriskOpenSource和CertifiedAsterisk安全漏洞(CVE-2016-7551)', NULL, '厂商补丁:目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：https://issues.asterisk.org/jira/browse/ASTERISK-26272', '中危', 'DigiumAsteriskOpenSource和CertifiedAsterisk都是美国Digium公司的开源电话交换机（PBX）系统软件。该软件支持语音信箱、多方语音会议、交互式语音应答(IVR)等。DigiumAsteriskOpenSource和CertifiedAsterisk中的chain_sip存在安全漏洞。远程攻击者可利用该漏洞造成拒绝服务（端口耗尽）。以下产品和版本受到影响：AsteriskOpenSource11.23.1之前的11.x版本，13.11.1之前的13.x版本；CertifiedAsterisk11.6-cert15之前的11.6版本和13.8-cert3之前的13.8版本。', '', 'CVE-2016-7551', '2018-04-03 13:54:12.148000');
