INSERT INTO "vulnerable" VALUES ('1644', '4ed46b5f-5dbe-4a44-82cb-79fb83e06cda', 'DigiumAsteriskOpenSource和CertifiedAsterisk安全漏洞(CVE-2014-4047)', NULL, '厂商补丁:目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://downloads.asterisk.org/pub/security/AST-2014-007.html', '中危', 'DigiumAsteriskOpenSource和CertifiedAsterisk都是美国Digium公司的开源电话交换机（PBX）系统软件。该软件支持语音信箱、多方语音会议、交互式语音应答(IVR)等。DigiumAsteriskOpenSource和CertifiedAsterisk中存在安全漏洞。远程攻击者可通过发送大量不完整的HTTP连接利用该漏洞造成拒绝服务。以下版本受到影响：AsteriskOpenSource1.8.28.1之前的1.8.x版本，11.10.1之前的11.x版本，12.3.1之前的12.x版本，CertifiedAsterisk1.8.15-cert6之前的1.8.15版本，11.6-cert3之前的11.6版本。', '', 'CVE-2014-4047', '2018-04-03 13:54:12.595000');
