INSERT INTO "vulnerable" VALUES ('2014', '621f145e-a005-4d5b-b4a9-b7221c418795', 'MS09-050: Vulnerabilities in SMBv2 Could Allow Remote Code Execution', 'MS09-050：SMBv2远程代码执行漏洞', '升级补丁MS09-050http://technet.microsoft.com/zh-CN/security/bulletin/ms09-050', '紧急', 'Windows系统捆绑了新版的SMB2协议实现，SRV2.SYS驱动没有正确地处理包含畸形SMB头结构数据的NEGOTIATEPROTOCOLREQUEST请求，如果远程攻击者在发送的SMB报文的ProcessIdHigh头字段中包含有畸形数据的话，就会在_Smb2ValidateProviderCallback()函数中触发越界内存引用，可能导致执行任意指令或导致系统蓝屏死机', '', '', '2018-04-03 22:27:21.889000');
