INSERT INTO "vulnerable" VALUES ('2035', 'a684063f-170f-430b-aa53-68bf8ac36707', 'SMTP Service STARTTLS Plaintext Command Injection', 'SMTP服务STARTTLS明文命令注入', '联系厂商获取相关补丁包。http://tools.ietf.org/html/rfc2487http://www.securityfocus.com/archive/1/516901/30/0/threaded', '中危', '远程邮件服务在协商加密通信隧道时，允许明文命令注入，这允许远程未认证的的攻击者在明文协议阶段注入命令，使其在密文协议阶段执行。', '', '', '2018-04-03 22:27:27.347000');
INSERT INTO "vulnerable" VALUES ('2036', 'd4a34f5e-41c5-4fac-bff2-93834f6755db', 'MS11-020: Vulnerability in SMB Server Could Allow Remote Code Execution (2508429)', 'MS11-020：SMB远程代码执行漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://technet.microsoft.com/zh-CN/security/bulletin/ms11-020', '紧急', 'MicrosoftServerMessageBlock(SMB)协议软件处理特制SMB报文时存在未验证远程代码执行漏洞。无需验证即可利用此漏洞，攻击者通过发送特制的SMB报文到运行服务器服务的计算机利用此漏洞。成功利用此漏洞可完全控制受影响系统', '', '', '2018-04-03 22:27:27.462000');
INSERT INTO "vulnerable" VALUES ('2037', 'c1f97fe2-d780-4a20-b4c6-a662f0b37c95', 'MS11-030: Vulnerability in DNS Resolution Could Allow Remote Code Execution (2509553)', 'MS11-030：DNS 解析远程代码执行漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://technet.microsoft.com/zh-CN/security/bulletin/ms11-030', '高危', 'WindowsDNS解析中存在漏洞。如果攻击者获得网络访问权限，然后创建一个自定义程序以将特制的LLMNR广播查询发送到目标系统，则该漏洞可能允许远程执行代码。', '', '', '2018-04-03 22:27:27.564000');
INSERT INTO "vulnerable" VALUES ('2038', '1bb0233f-eedc-4f77-bc6b-4c94dc59f7b6', ' MS11-030: Vulnerability in DNS Resolution Could Allow Remote Code Execution (2509553) (remote check)', 'MS11-030：DNS 解析远程代码执行漏洞', '目前厂商已经发布了WindowsXP,2003,Vista,2008,7,and2008R2平台的升级补丁包以修复此安全问题，补丁获取链接：http://technet.microsoft.com/zh-CN/security/bulletin/ms11-030', '紧急', 'WindowsDNS解析中存在漏洞。如果攻击者获得网络访问权限，然后创建一个自定义程序以将特制的LLMNR广播查询发送到目标系统，则该漏洞可能允许远程执行代码。', '', '', '2018-04-03 22:27:27.677000');
INSERT INTO "vulnerable" VALUES ('2039', 'a4834d26-a632-421e-90b1-505d6d1fd312', 'Apache 2.2 < 2.2.18 APR apr_fnmatch DoS', 'Apache版本低于2.2.18', '确保''IndexOptions''设置选项设置为''IgnoreClient''，或者升级Apache版本至2.2.18及以上。', '中危', 'Apache版本较低，由于发生在''apr_fnmatch''函数的错误，可能会受到DOS攻击。', '', '', '2018-04-03 22:27:27.796000');
INSERT INTO "vulnerable" VALUES ('2040', 'c67d9cd5-e50c-45d5-8d04-5fc650b6be09', 'MS11-035: Vulnerability in WINS Could Allow Remote Code Execution (2524426) (uncredentialed check)', 'MS11-035: WINS服务允许远程代码执行漏洞', '微软厂商已发布windows2003，2008和2008R2的相关补丁包。http://technet.microsoft.com/en-us/security/bulletin/ms11-035', '高危', '主机wins服务可远程执行shellcode代码。', '', '', '2018-04-03 22:27:27.914000');
INSERT INTO "vulnerable" VALUES ('2041', '30bf7372-c1cd-4b5d-8a68-d055ac5ca306', 'MS11-048: Vulnerability in SMB Server Could Allow Denial of Service (2536275)', 'MS11-048：SMB 拒绝服务漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：https://technet.microsoft.com/library/security/ms11-048', '高危', '当攻击者向存在该漏洞的系统发送特制的SMB数据包后，漏洞系统会产生拒绝服务现象。', '', '', '2018-04-03 22:27:28.023000');
INSERT INTO "vulnerable" VALUES ('2042', 'ff7cd815-5d82-44b3-a77c-8934f84c7033', ' Oracle Database Unsupported', '当前ORACLE数据库版本厂商已不支持', '升级数据库版本至当前最新版本，至少升级至厂家支持的版本。', '紧急', '根据远程主机安装的数据库版本，厂家已不再支持，将会缺少安全补丁包，因此，可能存在安全威胁。', '', '', '2018-04-03 22:27:28.138000');
INSERT INTO "vulnerable" VALUES ('2043', 'd932277e-9de0-4d2a-bfff-f6cf93786018', 'Apache HTTP Server Byte Range DoS', 'Apache HTTP Server拒绝服务漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：https://issues.apache.org/bugzilla/show_bug.cgi?id=51714', '高危', '当处理包含大量Ranges头的HTTP请求时，ByteRange过滤器存在一个错误，攻击者可以向服务器发送特制HTTP请求，消耗大量内存，造成应用程序崩溃。', '', '', '2018-04-03 22:27:28.262000');
INSERT INTO "vulnerable" VALUES ('2044', '604675b2-f29b-4317-8620-a93ecd0d0e8a', 'Microsoft Windows SMB LsaQueryInformationPolicy Function SID Enumeration Without Credentials', 'Microsoft Windows SMB LsaQueryInformationPolicy 函数 SID枚举漏洞', '打开注册表，进行以下设置：-HKLM\SYSTEM\CurrentControlSet\Control\LSA\RestrictAnonymous=1', '中危', '通过模拟调用LsaQueryInformationPolicy()，无需通过认证即可获取目标主机SID(SecurityIdentifier)，通过SID能得获取系统用户列表。', '', '', '2018-04-03 22:27:28.368000');
INSERT INTO "vulnerable" VALUES ('2045', 'd0704514-ab77-46d0-9486-b966c4e719d8', 'Apache 2.2 < 2.2.21 mod_proxy_ajp DoS', 'Apache版本低于2.2.21 导致mod_proxy_ajp 漏洞', '升级Apache版本至2.2.21及以上。', '中危', 'Apache版本较低，由于发生在''mod_proxy_ajp''函数的错误，可能会受到DOS攻击。', '', '', '2018-04-03 22:27:28.466000');
