INSERT INTO "vulnerable" VALUES ('2185', '67ca2697-909a-4fbc-80e3-5c2269f72e17', ' MySQL Default Account Credentials', 'MySQL默认账号密码', '删除受影响的默认账户，或者更换符合密码复杂度的口令', '高危', 'MySQL数据库服务器存在默认账户，该漏洞源于scrutinizer账户的默认账户', '', '', '2018-04-03 22:34:23.872000');
