INSERT INTO "vulnerable" VALUES ('2187', 'cf663f23-7040-48c0-ac2a-008c7750cb5c', ' IBM WebSphere Application Server 6.1 < Fix Pack 47 Multiple Vulnerabilities', 'IBMWebSphere应用服务器版本低于补丁包47版本，存在多种安全漏洞', '升级IBMWebSphere应用服务器版本，应用补丁包47升级相关链接：http://www.nessus.org/u?187690fdhttps://www-304.ibm.com/support/docview.wss?uid=swg21647522http://www-01.ibm.com/support/docview.wss?uid=swg24035508https://www-304.ibm.com/support/docview.wss?&uid=swg27004980#ver61', '中危', 'IBMWebSphereApplicationServer(WAS)为6.1之前版本，低于补丁包47要求版本，存在多个安全漏洞。', '', '', '2018-04-03 22:34:24.066000');
INSERT INTO "vulnerable" VALUES ('2188', '8cf867c4-4b31-4f16-8ea5-357237b9361d', 'Apache < 1.3.29 Multiple Modules Local Overflow', 'Apache版本低于1.3.29，多种模块存在缓冲区溢出漏洞', '目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载：http://httpd.apache.org/download.cgi', '高危', 'Apache是一款广泛使用的开放源代码WEB服务程序。Apache的mod_alias和mod_rewrite模块中存在缓冲区溢出漏洞，可能允许本地攻击者在有漏洞的主机上执行任意代码。如果正则表达式配置了多于9个捕获括号的话，就可能导致mod_alias和mod_rewrite模块出现缓冲区溢出。攻击者可以通过创建特制的配置文件导致拒绝服务(崩溃)或执行任意代码。', '', '', '2018-04-03 22:34:24.173000');
INSERT INTO "vulnerable" VALUES ('2189', '5d8c6ee5-c8be-41a1-a80f-a094f21d90f4', 'Apache mod_proxy Content-Length Overflow', 'Apachemod_proxy远程缓冲区溢出漏洞', '升级Apache版本至1.3.32及以上版本相关链接：http://archives.neohapsis.com/archives/fulldisclosure/2004-06/0276.htmlhttp://archives.neohapsis.com/archives/fulldisclosure/2004-06/0279.html', '紧急', 'mod_proxy是一个可在Apache使用的代理模块。mod_proxy在处理负的Content-Length值时存在问题，远程攻击者可以利用这个漏洞进行缓冲区溢出攻击，可能以Apache进程权限在系统上执行任意指令。连接远程服务器使其返回非法(负值)的Content-Length，可使Mod_proxy触发基于整数的溢出，会导致Apache子进程崩溃。问题存在于proxy_util.c中：n=ap_bread(f，buf，MIN((int)buf_size，(int)(len-total_bytes_rcvd)));这里len可为负值。在旧的OpenBSD/FreeBSD系统中可利用memcpy的反向拷贝实现来执行任意指令。新的BSD系统中由于攻击者可以控制3个任意字节也能用于执行任意指令。如果defineAP_ENABLE_EXCEPTION_HOOK启用的情况下，可能在任何平台都可被利用。', '', '', '2018-04-03 22:34:24.290000');
INSERT INTO "vulnerable" VALUES ('2190', '0b1fba2f-8226-4c21-b4e2-b68af1fe1cb5', 'IBM WebSphere Application Server SOAP Connector Error Page XSS', 'IBMWebSphere应用服务器SOAP错误页码跨站脚本攻击漏洞', '根据当前IBMWebSphere当前版本，升级至5.1.1/6.0.2及以上版本相关链接：http://www.securityfocus.com/archive/1/450704/30/0/threadedhttp://www-1.ibm.com/support/search.wss?rs=0&q=PK16602&apar=only', '中危', 'IBMWebSphereApplicationServer5.0.2及之前版本,5.1.1.12之前的5.1.x,以及6.0.2到6.0.2.7版本的SOAP端口(8880/tcp)上的500InternalServerError页中存在跨站脚本攻击漏洞。远程攻击者可以借助该页的FAULTACTOR元素中包含的URI，注入任意Web脚本或HTML。', '', '', '2018-04-03 22:34:24.407000');
INSERT INTO "vulnerable" VALUES ('2191', 'a702f18b-0606-48ad-99f7-e58945329051', 'Apache < 1.3.37 mod_rewrite LDAP Protocol URL Handling Overflow', 'Apache版本低于1.3.37，mod_rewrite模块单字节缓冲区溢出漏洞', '升级Apache版本至1.3.37及以上版本相关链接：http://seclists.org/fulldisclosure/2006/Jul/671http://www.securityfocus.com/archive//443870', '高危', 'Apache是一款开放源代码WEB服务程序。Apache的mod_rewrite模块在转义绝对URI主题时存在单字节缓冲区溢出漏洞，攻击者可能利用此漏洞在服务器上执行任意指令。mod_rewrite模块的escape_absolute_uri()函数分离LDAPURL中的令牌时，会导致在字符指针数组以外写入指向用户控制数据的指针，这样就可能完全控制受影响的主机。', '', '', '2018-04-03 22:34:24.538000');
INSERT INTO "vulnerable" VALUES ('2192', 'efa77eb2-42b2-4962-9304-380635d2c672', 'VNC Server Unauthenticated Access', 'VNC服务未经身份验证访问漏洞', '禁用“无验证”安全类型', '高危', 'VNC服务允许攻击者不通过访问控制即连接远程主机', '', '', '2018-04-03 22:34:24.657000');
INSERT INTO "vulnerable" VALUES ('2193', '3d26c546-1d64-4d22-9485-2cf5b1df4a4c', 'VNC Server Unauthenticated Access: Screenshot', 'VNC服务器未经身份验证的访问:屏幕截图', '禁用“无验证”安全类型', '高危', 'VNC服务允许攻击者不通过访问控制即连接远程主机，这可能运行攻击者连接服务并进行屏幕截图操作', '', '', '2018-04-03 22:34:24.761000');
INSERT INTO "vulnerable" VALUES ('2194', '768e8a29-47a3-418b-a19a-a5ebfdd7a64e', 'JBoss Enterprise Application Platform (EAP) Status Servlet Request Remote Information Disclosure', 'JBossEAP信息泄露漏洞', '升级JBossEAP版本至4.2.0.CP09/4.3.0.CP08相关链接：https://bugzilla.redhat.com/show_bug.cgi?id=457757https://bugzilla.redhat.com/show_bug.cgi?id=585900', '中危', 'JBossEnterpriseApplicationPlatformJBossEnterpriseApplicationPlatform是一款企业级应用平台，又称JBossEAP。JBossEAP4.2.0.CP03之前版本以及4.3.0.CP01之前的4.3.0版本存在信息泄露漏洞。上述版本允许远程攻击者通过对statusservlet的一个请求，例如一个full=true查询串，获得关于&#34;配置的web内容&#34;的敏感信息。', '', '', '2018-04-03 22:34:24.862000');
INSERT INTO "vulnerable" VALUES ('2195', '0f653a81-2b44-48bc-8941-4bfaa75df1ac', ' VMware ESX / ESXi Unsupported Version Detection', 'VMwareESX/ESXi版本不支持', '升级VMwareESX/ESXi至最新版本相关链接：https://www.vmware.com/support/policies/lifecycle.htmlhttps://www.vmware.com/files/pdf/support/Product-Lifecycle-Matrix.pdf', '紧急', '厂商已不支持安装在远程主机的VMwareESXorESXi版本，可能存在多种安全威胁。', '', '', '2018-04-03 22:34:24.983000');
INSERT INTO "vulnerable" VALUES ('2196', '12da4e57-4b1a-4ad8-baa2-140df784f76c', ' /doc Directory Browsable', '/doc目录可被浏览', '为/docdirectory设置访问控制权限，如果您使用Apache，您可以需要如此设置：<Directory/usr/doc>允许对本地目录进行访问权限控制，明确允许/拒绝策略。相关链接：http://projects.webappsec.org/Directory-Indexing', '中危', 'DebianGNU/Linux上Apache的一个默认配置设置ServerRoot到/usr/doc存在漏洞。远程攻击者利用该漏洞读取整个服务器的文档文件', '', '', '2018-04-03 22:34:25.101000');
INSERT INTO "vulnerable" VALUES ('2197', '118548d8-daed-4046-905a-1b5cb683c60a', 'Multiple Web Server printenv CGI Information Disclosure', '多种Web服务printenvCGI信息泄露', '从/cgi-bin移除printenv', '中危', '远程WEB服务存在''test-cgi''测试脚本，printenvCGI可返回其环境变量，这给攻击者很多信息,如安装目录,服务器的IP地址(如果NAT实现),服务器管理员的电子邮件地址,服务器和模块版本中,shell环境变量……', '', '', '2018-04-03 22:34:25.272000');
INSERT INTO "vulnerable" VALUES ('2198', 'ae8f802e-f981-449e-9320-415b05589623', 'Oracle Database Listener Program (tnslsnr) Service Blank Password', 'Oracle数据库侦听器程序(tnslsnr)服务密码空白', '使用lsnrctrlCHANGE_PASSWORD命令为侦听器指定一个密码。', '中危', '远程Oracle侦听器程序(tnslsnr)没有指定密码，攻击者可以利用这一点来任意关闭它,从而阻止合法用户使用它。', '', '', '2018-04-03 22:34:25.393000');
INSERT INTO "vulnerable" VALUES ('2199', 'd67387dd-b96d-4b9f-8a34-9cd74eb3741f', 'Oracle 9iAS DMS / JPM Pages Anonymous Access', 'Oracle9iASDMS/JPM页面匿名访问', '编辑http.conf，限制对/dms0的访问', '中危', 'Oracle9iApplicationServer1.0.2.x版本的默认配置存在漏洞。远程匿名用户可以无认证访问敏感服务，包括可以被用来控制Java进程的DynamicMonitoringServices(1)dms0、(2)dms/DMSDump、(3)servlet/DMSDump、(4)servlet/Spy、(5)soap/servlet/Spy和(6)dms/AggreSpy;以及OracleJavaProcessManager(7)oprocmgr-status和(8)oprocmgr-service', '', '', '2018-04-03 22:34:25.505000');
INSERT INTO "vulnerable" VALUES ('2200', '74d53abe-9f81-4f66-891a-7abc4775e3d9', 'Oracle 9iAS Java Process Manager /oprocmgr-status Anonymous Process Manipulation', 'Oracle9iASJavaProcessManager/oprocmgr-status匿名访问操作', '编辑http.conf，限制对/oprocmgr-status的访问', '中危', 'Oracle9iApplicationServer1.0.2.x版本的默认配置存在漏洞。远程匿名用户可以无认证访问敏感服务，包括可以被用来控制Java进程的DynamicMonitoringServices(1)dms0、(2)dms/DMSDump、(3)servlet/DMSDump、(4)servlet/Spy、(5)soap/servlet/Spy和(6)dms/AggreSpy;以及OracleJavaProcessManager(7)oprocmgr-status和(8)oprocmgr-service', '', '', '2018-04-03 22:34:25.606000');
INSERT INTO "vulnerable" VALUES ('2201', '1d630155-481f-4118-9d6b-ad863a4ce750', ' Apache Chunked Encoding Remote Overflow', 'Apache分块编码远程溢出', '升级Apacheweb服务版本至1.3.26or2.0.39及以上版本相关链接：http://httpd.apache.org/info/security_bulletin_20020617.txthttp://httpd.apache.org/info/security_bulletin_20020620.txt', '高危', 'ApacheWebServer是一款非常流行的开放源码、功能强大的Web服务器程序，由ApacheSoftwareFoundation开发和维护。它可以运行在多种操作系统平台下，例如Unix/Linux/BSD系统以及Windows系统。Apache在处理以分块(chunked)方式传输数据的HTTP请求时存在设计漏洞，远程攻击者可能利用此漏洞在某些Apache服务器上以Web服务器进程的权限执行任意指令或进行拒绝服务攻击。', '', '', '2018-04-03 22:34:25.730000');
INSERT INTO "vulnerable" VALUES ('2202', '72eb3eca-3454-44d4-81eb-4063b921aef3', 'Apache < 1.3.27 Multiple Vulnerabilities (DoS, XSS)', 'Apache版本低于1.3.27，存在多种安全漏洞（DOS，XSS）', '升级Apacheweb服务版本至1.3.27及以上版本相关链接：http://archives.neohapsis.com/archives/bugtraq/2002-10/0195.htmlhttp://archives.neohapsis.com/archives/vulnwatch/2002-q4/0012.htmlhttp://archives.neohapsis.com/archives/bugtraq/2002-11/0137.htmlhttp://archives.neohapsis.com/archives/vulnwatch/2002-q4/0003.html', '高危', 'Apache版本低于1.3.27，可能导致多种安全漏洞。', '', '', '2018-04-03 22:34:25.946000');
INSERT INTO "vulnerable" VALUES ('2203', '8aad1c41-502a-491d-af5c-956c8f985b23', 'Oracle 9iAS soapdocs Directory Remote Information Disclosure', 'Oracle9iASsoapdocs目录远程信息泄露', '从Oracle9iAS''http.conf''中删除''soapdocs''别名', '中危', '默认SOAP安装包含的安装脚本，可能访问Oracle9iASApplicationServer''sSOAP文件目录，这些文件对攻击者来说可以判定那些应用服务程序在使用。', '', '', '2018-04-03 22:34:26.058000');
INSERT INTO "vulnerable" VALUES ('2204', '2cc474b2-0c32-4165-a56b-72401aaf18c8', 'Oracle 9iAS XSQLServlet soapConfig.xml Authentication Credentials Disclosure', 'Oracle9iASXSQLServletsoapConfig.xml身份认证凭证泄露', '临时解决方法：如果您不能立刻安装补丁或者升级，Oracle建议您采取以下措施以降低威胁：*对两个配置文件设置正确的访问权限，禁止匿名用户访问。厂商补丁：目前厂商还没有提供补丁或者升级程序，建议用户随时关注厂商的主页以获取最新版本：http://metalink.oracle.com/', '中危', 'Oracle9iAS包含了两个重要的配置文件叫&#34;XSQLConfig.xml&#34;和&#34;soapConfig.xml&#34;，它们分别是XSQL和SOAP组件的配置文件，其中存放了一些敏感信息比如数据库服务器主机名、数据库用户名和口令等等。Oracle9iAS在这两个敏感文件的访问控制上存在问题，导致远程攻击者可能得到其中的信息。Oracle9iAS对这些配置文件的访问没有做任何认证，任何客户端可以轻易地通过Web虚拟目录访问到这两个文件。', '', '', '2018-04-03 22:34:26.160000');
INSERT INTO "vulnerable" VALUES ('2205', 'c68e9cda-f110-4724-bf29-af7890a33772', 'Oracle 9iAS Nonexistent .jsp File Request Error Message Path Disclosure', 'Oracle9iAS不存在的.jsp文件请求错误信息路径泄露漏洞', '1、确保虚拟路径的URL不同于实际的目录路径。同时,不要使用<servletzonepath>目录”ApJServMount<servletzonepath><servletzone>''来存储数据或文件。2、升级到Oracle9ias1.1.2.0.0也可解决这个问题。相关链接：http://www.nessus.org/u?8d439be5http://www.nessus.org/u?97653726', '中危', 'Oracle9i应用服务器1.0.2版本存在漏洞。远程攻击者可以借助对不存在.JSP文件的请求获取服务器根目录下文件的物理路径。该漏洞在出错消息中泄露了路径名。', '', '', '2018-04-03 22:34:26.288000');
INSERT INTO "vulnerable" VALUES ('2206', 'cf652848-6d06-436a-902e-74b59daff37e', 'Oracle 9iAS Default SOAP Configuration Unauthorized Application Deployment', 'Oracle9iASSOAP默认配置漏洞', '临时解决方法：如果您不能立刻安装补丁或者升级，DavidLitchfield建议您采取以下措施以降低威胁：*如果您不需要SOAP组件，您应该立刻关闭SOAP。编辑&#34;$ORACLE_HOME/Apache/Jserv/etc/jserv.conf&#34;配置文件，删除或者注释掉下面的行：ApJServGroupgroup211$ORACLE_HOME/Apache/Jserv/etc/jservSoap.propertiesApJServMount/soap/servletajpv12://localhost:8200/soapApJServMount/dms2ajpv12://localhost:8200/soapApJServGroupMount/soap/servletbalance://group2/soap*如果您需要SOAP，您应该关闭它的自动deploy功能。编辑&#34;$ORACLE_HOME/soap/werbapps/soap/WEB-INF/config/soapConfig.xml&#34;文件，加入以下的行：*限制访问SOAPdeploy/undeploy功能的用户，应该只有管理员和可信用户可以使用此功能。厂商补丁：目前厂商已经提供了解决方案以修复这个安全问题，请参看下列厂商安全公告http://technet.oracle.com/deploy/security/pdf/ias_soap_alert.pdf', '高危', 'Oracle9iAS是一种大型的关系数据库系统，由Oracle公司开发和维护。它支持简单对象访问协议(SOAP)协议，它是一种基于XML的协议，Oracle用它来对数据库服务进行Web管理。Oracle9iAS1.0.2.2.1缺省安装并使用了SOAP组件，远程攻击者无须认证即可配置或关闭(deploy/undploy)SOAP的提供者及服务，与其它漏洞结合，攻击者可能进行更进一步的破坏。', '', '', '2018-04-03 22:34:26.402000');
INSERT INTO "vulnerable" VALUES ('2207', 'cf2f2939-feb9-4618-8861-4f10163be3dc', 'OpenSSL < 0.9.6j / 0.9.7b Multiple Vulnerabilities', 'OpenSSL版本低于0.9.6j/0.9.7b，存在多种安全漏洞', '升级版本至0.9.6j(0.9.7b)及以上版本相关链接：http://www.openssl.org/news/secadv_20030219.txthttp://eprint.iacr.org/2003/052/', '中危', '远程主机的OpenSSL版本已低于0.9.6j/0.9.7b，这可能遭受多种安全威胁攻击，例如中间人攻击等', '', '', '2018-04-03 22:34:26.504000');
INSERT INTO "vulnerable" VALUES ('2208', '5f2d7abc-c348-48da-a8ac-bd53fc26ad7e', 'Oracle 9iAS PL/SQL Gateway Web Admin Interface Null Authentication', 'Oracle9iASApachePL/SQL模块Web管理页面访问控制漏洞', '临时解决方法：如果您不能立刻安装补丁或者升级，建议您采取以下措施以降低威胁：*在/Apache/modplsql/cfg/wdbsvr.app文件中对可以访问的用户名等相关信息进行设置，确信只有合法用户才能访问。厂商补丁：目前厂商已经提供了解决方案以修复这个安全问题，请到厂商的主页下载：http://otn.oracle.com/deploy/security/pdf/ias_modplsql_alert.pdf', '高危', 'Oracle9iAS的Web服务部分是由增加了PL/SQL支持模块的Apache服务器提供。主机上相关的用于管理的Web页面可以使用户修改数据库访问描述符和缓存设置。Oracle9iAS的Web管理页面访问控制上存在问题，远程攻击者可能利用此漏洞更改系统设置，造成拒绝服务攻击。默认安装情况下，对于那些管理页面的访问不需要认证，这就使任意远程攻击者可以通过访问那些管理页面执行某些操作，更改PL/SQL应用程序或者使合法用户不能访问服务器程序。', '', '', '2018-04-03 22:34:26.620000');
INSERT INTO "vulnerable" VALUES ('2209', '859aa7df-68dd-4571-8f8c-492f11de059a', 'Apache < 1.3.28 Multiple Vulnerabilities (DoS, ID)', 'Apache版本低于1.3.28，存在多种安全漏洞（Oos，ID）', '临时解决方法：如果您不能立刻安装补丁或者升级，建议您采取以下措施以降低威胁：*在CustomLog或ErrorLog中禁用rotatelog工具。厂商补丁：ApacheSoftwareFoundation--------------------------目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载：ApacheSoftwareFoundationUpgradeApachehttpd1.3.28http://httpd.apache.org/download.cgi', '高危', 'ApacheHTTP服务器是流行的开源WEB服务器程序，可使用在Unix和Windows操作系统下。ApacheHTTP1.3.28修补了多个漏洞，这些漏洞包括拒绝服务攻击、文件描述符泄露、日志记录失败等问题。', '', '', '2018-04-03 22:34:26.715000');
INSERT INTO "vulnerable" VALUES ('2210', 'abf45a39-cc17-4503-966a-6354c026b54b', 'OpenSSL < 0.9.6m / 0.9.7d Multiple Remote DoS', 'OpenSSL版本低于0.9.6m/0.9.7d，存在多种远程Dos攻击', '升级版本至0.9.6m/0.9.7d及以上版本相关链接：http://www.openssl.org/news/secadv_20040317.txthttp://archives.neohapsis.com/archives/bugtraq/2004-03/0165.html', '中危', '根据主机的OpenSSL版本信息，可能导致攻击者对远程主机发起拒绝服务攻击', '', '', '2018-04-03 22:34:26.857000');
INSERT INTO "vulnerable" VALUES ('2211', 'ab307af7-cdcc-4e8a-966d-1719dd58f4b6', 'mod_ssl ssl_util_uuencode_binary Remote Overflow', 'ApacheMod_SSLSSL_Util_UUEncode_Binary堆栈缓冲区溢出漏洞', '升级Apache版本至2.8.18(Apache1.3)或者2.0.50.', '高危', 'Mod_SSL是Apache服务器上的SSL实现，用来为ApacheWeb服务器提供加密支持。Apachemod_ssl2.8.17及其以前版本的实现中模块ssl_util.c的‘ssl_util_uuencode_binary’函数存在问题，远程攻击者可以利用使用该函数的ssl_engine_kernel.c模块进行拒绝服务攻击或可能以WEB进程执行任意指令。', '', '', '2018-04-03 22:34:26.947000');
INSERT INTO "vulnerable" VALUES ('2212', 'ac00e1ba-c45e-41de-a6eb-3e9b3ffdf77a', 'Apache < 1.3.31 / 2.0.49 Socket Connection Blocking Race Condition DoS', 'Apache版本低于1.3.31/2.0.49，存在拒绝服务攻击漏洞', '升级Apache版本至2.0.49or1.3.31。相关链接：http://archives.neohapsis.com/archives/bugtraq/2004-03/0192.html', '中危', '运行在远程主机上的Apache版本低于1.3.31/2.0.49，这些版本容易受到拒绝服务攻击，攻击者可以通过一个极少使用的端口连接侦探SOCKET，从而阻断其它新的连接。这种问题可影响一些AIXSolaris和Tru64系统版本，不会影响FreeBSD或Linux', '', '', '2018-04-03 22:34:27.071000');
INSERT INTO "vulnerable" VALUES ('2213', '49bfc049-79be-4509-bdf3-12f80fb7151c', 'Apache mod_ssl ssl_engine_log.c mod_proxy Hook Function Remote Format String', 'Apachemod_sslssl_engine_log.cmod_proxyHookFunction远程格式化字符串漏洞', '升级mod_ssl版本至2.8.19及以上相关链接：http://marc.info/?l=apache-modssl&m=109001100906749&w=2http://marc.info/?l=bugtraq&m=109005001205991&w=2', '高危', 'mod_ssl版本低于2.8.19，远程主机的日志函数模块存在格式化字符串，使得攻击者可以执行任意代码。', '', '', '2018-04-03 22:34:27.184000');
INSERT INTO "vulnerable" VALUES ('2214', '4a719e66-d8a8-440d-a25a-114d05ce015d', 'OpenSSL < 0.9.6e Multiple Vulnerabilities', 'OpenSSL版本低于0.9.6e，存在多种安全漏洞', '升级OpenSSL版本至0.9.6e及以上版本', '高危', '远程主机存在多个与SSL相关的安全漏洞，例如在64位系统下，OpenSSL在很多缓冲区中对于整形数的ASCII表示方法不正确，可能导致攻击者进行拒绝服务攻击或通过溢出攻击在主机上执行任意指令、缓冲区溢出导致攻击者可执行任意代码、OpenSSL的ASN1解释器在处理无效的编码方式时存在漏洞，远程攻击者可能利用此漏洞对使用了ASN1库的应用程序进行拒绝服务攻击。', '', '', '2018-04-03 22:34:27.320000');
INSERT INTO "vulnerable" VALUES ('2215', '10c99d61-fa2b-43dc-b26c-bac2c99062e7', 'OpenSSL < 0.9.6f Denial of Service', 'OpenSSL版本低于0.9.6f，存在拒绝服务攻击漏洞', '升级OpenSSL版本至0.9.6f及以上版本。相关链接：http://cvs.openssl.org/chngview?cn=7659http://www.securityfocus.com/archive/1/339948', '中危', 'OpenSSL版本低于0.9.6f，可能使远程攻击者通过伪造一个SSLv2CLIENT_MASTER_KEY信息，发起拒绝服务攻击。', '', '', '2018-04-03 22:34:27.532000');
INSERT INTO "vulnerable" VALUES ('2216', 'a27aadc0-f51b-4dcc-b520-e319ae0332f4', 'OpenSSL < 0.9.6k Denial of Service', 'OpenSSL版本低于0.9.6k，存在拒绝服务攻击漏洞', '升级OpenSSL版本至0.9.6k及以上版本。', '中危', 'OpenSSL版本低于0.9.6k，可能使远程攻击者通过提交一个无效的客户端证书，发起拒绝服务攻击。', '', '', '2018-04-03 22:34:27.637000');
INSERT INTO "vulnerable" VALUES ('2217', '0729ae1b-4335-49d7-b27e-0b387b79d5b0', 'OpenSSL < 0.9.6l Denial of Service', 'OpenSSL版本低于0.9.6l，存在拒绝服务攻击漏洞', '升级OpenSSL版本至0.9.6l及以上版本。相关链接：http://www.openssl.org/news/secadv_20031104.txthttp://marc.info/?l=bugtraq&m=106796246511667&w=2', '中危', 'OpenSSL版本低于0.9.6l，可能使远程攻击者通过提交一个无效的客户端证书，发起拒绝服务攻击。', '', '', '2018-04-03 22:34:27.758000');
INSERT INTO "vulnerable" VALUES ('2218', '07158b96-04d3-49fb-8aa1-21a7e002ffdd', ' OpenSSL < 0.9.6m / 0.9.7d Denial of Service', 'OpenSSL版本低于0.9.6m/0.9.7d，存在拒绝服务攻击漏洞', '升级OpenSSL版本至0.9.6m/0.9.7d及以上版本。相关链接：http://www.us-cert.gov/cas/techalerts/TA04-078A.htmlhttp://www.openssl.org/news/secadv_20040317.txthttp://marc.info/?l=bugtraq&m=107953412903636&w=2', '中危', 'OpenSSL版本低于0.9.6m/0.9.7d，可能使远程攻击者发送超过规定长度的Kerberos凭证，使服务器崩溃。', '', '', '2018-04-03 22:34:27.867000');
INSERT INTO "vulnerable" VALUES ('2219', 'efe279c3-5e59-4619-ba4b-3f38a2f81beb', 'OpenSSL 0.9.6 CA Basic Constraints Validation Vulnerability', 'OpenSSL证书校验漏洞', '升级OpenSSL版本至0.9.7及以上版本相关链接：http://www.nessus.org/u?8e41b7c3', '高危', 'OpenSSL是一款开放源代码的实现了SSL及相关加密技术的软件包。OpenSSL0.9.6版本可能没有校验中间有符号的计算机存储证书的基本限制，这会允许远程攻击者可以借助一个中间人攻击，骗取可靠网址的证书。', '', '', '2018-04-03 22:34:27.971000');
INSERT INTO "vulnerable" VALUES ('2220', 'b0b31272-8dd5-493b-a4ff-7ec715e43458', 'OpenSSL < 0.9.7-beta3 Buffer Overflow', 'OpenSSL版本低于0.9.7-beta3，存在缓冲区溢出漏洞', '临时解决方法：如果您不能立刻安装补丁或者升级，建议您采取以下措施以降低威胁：*服务器端暂时禁止使用Kerberos厂商补丁：OpenSSLProject已经为此发布了一个安全公告(secadv_20020730)以及相应补丁:secadv_20020730：OpenSSLSecurityAdvisory[30July2002]链接：http://www.openssl.org/news/secadv_20020730.txt补丁下载：OpenSSL0.9.7-beta3:http://www.openssl.org/source/openssl-0.9.7-beta3.tar.gz', '高危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。OpenSSL的实现上存在缓冲区溢出漏洞，远程攻击者可能利用溢出攻击在服务器端或客户端执行任意指令。远程攻击者可能发送超长的SSL版本3的客户端密钥，溢出服务器端守护进程的缓冲区，从而以守护进程的权限执行任意指令。这个漏洞只影响OpenSSL0.9.7到0.9.7-beta3之间(不包括beta3)并打开了Kerberos选项的版本。', '', '', '2018-04-03 22:34:28.089000');
INSERT INTO "vulnerable" VALUES ('2221', '80fb592b-995c-45a5-9085-66d57faddae9', ' OpenSSL < 0.9.8 Weak Default Configuration', 'OpenSSL版本低于0.9.8，存在弱默认配置漏洞', '升级OpenSSL版本至0.9.8及以上版本相关链接：https://bugs.launchpad.net/ubuntu/+source/openssl/+bug/19835http://www.ubuntu.com/usn/usn-179-1/', '中危', 'SSL是SecureSocketLayer(安全套接层协议)的缩写，可以在Internet上提供秘密性传输。OpenSSL0.9.8版本之前的默认配置使用MD5创建消息摘要，而非使用强加密算法，从而使远程攻击者更容易使用有效的证书认证机构的签名来伪造证书。', '', '', '2018-04-03 22:34:28.197000');
INSERT INTO "vulnerable" VALUES ('2222', '27049e34-5698-4e9b-8c90-1cd663be278d', 'OpenSSL < 0.9.8f Multiple Vulnerabilities', 'OpenSSL版本低于0.9.8f，存在多个安全漏洞', '升级OpenSSL版本至0.9.8f及以上版本相关链接：http://cvs.openssl.org/chngview?cn=16275http://openssl.org/news/patch-CVE-2007-3108.txthttp://www.kb.cert.org/vuls/id/RGII-74KLP3http://www.openssl.org/news/secadv_20071012.txt', '高危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中，此版本存在以下两个漏洞：（1）OpenSSL0.9.8e及之前版本中的某些RSA的实现上存在可重建密钥的漏洞，OpenSSL基于SSLeay，提供RSA算法的加密支持，本地攻击者可能利用此漏洞通过RSA算法解密加密信息的内容。多种使用了OpenSSL的产品可能受此漏洞影响。（2）OpenSSL的DTLS支持中存在漏洞，攻击者可以创建能够触发堆溢出的恶意客户端或服务器，导致执行任意指令，请注意这个漏洞仅影响使用DTLS的应用程序。', '', '', '2018-04-03 22:34:28.287000');
INSERT INTO "vulnerable" VALUES ('2223', 'c73e8a0f-039b-4b8c-927d-925b77a1d7de', 'OpenSSL < 0.9.8i Denial of Service', 'OpenSSL版本低于0.9.8i，存在拒绝服务攻击漏洞', '升级OpenSSL版本至0.9.8i及以上版本相关链接：http://cvs.openssl.org/chngview?cn=17369http://rt.openssl.org/Ticket/Display.html?id=1679&user=guest&pass=guest', '中危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。如果在ClientHello报文之前发送了DTLSChangeCipherSpec报文，就可能在OpenSSL的ssl/s3_pkt.c文件中触发空指针引用，导致拒绝服务的情况。', '', '', '2018-04-03 22:34:28.435000');
INSERT INTO "vulnerable" VALUES ('2224', 'e07b324d-7aa3-45b9-a0fb-482cec3e3aa0', ' OpenSSL < 0.9.8j Signature Spoofing', 'OpenSSL版本低于0.9.8j，存在签名验证漏洞', '升级OpenSSL版本至0.9.8j及以上版本相关链接：http://www.us-cert.gov/cas/techalerts/TA09-133A.html', '中危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。某些OpenSSL函数在验证DSA和ECDSA密钥签名时没有正确地验证函数EVP_VerifyFinal()的返回值，畸形的签名可能被处理为有效签名而不是错误。恶意服务器或中间人攻击者可以在证书链中向客户端特制SSL/TLS签名来绕过有效性验证，诱骗客户端错误的信任恶意站点。', '', '', '2018-04-03 22:34:28.549000');
INSERT INTO "vulnerable" VALUES ('2225', '835e6248-6f69-436b-b7d9-c29ce949759f', 'OpenSSL < 0.9.8k Denial of Service', 'OpenSSL版本低于0.9.8k，存在拒绝服务攻击漏洞', '升级OpenSSL版本至0.9.8k及以上版本相关链接：http://www.openssl.org/news/secadv_20090325.txt', '中危', '主机OpenSSL版本低于0.9.8K,这使得远程攻击者通过伪造ASN.1数据发起拒绝服务攻击', '', '', '2018-04-03 22:34:28.655000');
INSERT INTO "vulnerable" VALUES ('2226', 'cf1d6b20-4dfe-490a-a3b4-4e502a4f32c0', 'OpenSSL < 0.9.8l Multiple Vulnerabilities', 'OpenSSL版本低于0.9.8l，存在多个安全漏洞', '升级OpenSSL版本至0.9.8l及以上版本相关链接：http://voodoo-circle.sourceforge.net/sa/sa-20090326-01.htmlhttp://www.openssl.org/news/secadv_20090325.txthttp://voodoo-circle.sourceforge.net/sa/sa-20091012-01.htmlhttp://rt.openssl.org/Ticket/Display.html?id=1930&user=guest&pass=guesthttp://rt.openssl.org/Ticket/Display.html?id=1931&user=guest&pass=guesthttp://cvs.openssl.org/chngview?cn=18187http://cvs.openssl.org/chngview?cn=18188', '中危', '主机OpenSSL版本低于0.9.8l,这使得主机存在多个安全漏洞。', '', '', '2018-04-03 22:34:28.760000');
INSERT INTO "vulnerable" VALUES ('2227', 'b8f87d67-2120-495d-9275-bd628f4955ff', 'IBM Tivoli Storage Manager Multiple Remote Overflows', 'IBMTivoliStorageManager多个远程缓冲区溢出漏洞', '目前厂商已经发布了升级补丁以修复这个安全问题，请到厂商的主页下载：http://www-1.ibm.com/support/docview.wss?uid=swg21250261', '紧急', 'TivoliStorageManager是一种遵循ANSISAN标准的可扩展解决方案，用于发现、监控和管理企业SAN架构组件，并可分配和自动操纵企业的附加磁盘存储资源。TivoliStorageManager服务在处理发送到TCP/1500端口的消息时存在多个内存访问拷贝漏洞，远程攻击者可能利用这些漏洞导致服务崩溃或执行任意指令。能够触发漏洞的消息格式为[index][size]，其中index字段指定了到消息体特定字段的整数偏移，size字段指定了index字段的大小。由于没有验证index字段，因此攻击者可以强制服务读过报文的末尾，到达未分配的内存，导致拒绝服务。通常size字段都会经过检查以确保不会超过数据所拷贝目标缓冲区的边界，但在有些情况下不会检查size文件：1初始的注册请求包含有一个用于指定语言的字段。通常这个字符串为dscenu.txt，服务器会验证语言字符串不会超过0x100字节。但如果语言字符串的第一个字节为0x18，就不会进行这个检查，导致溢出固定大小的缓冲区。2SmExecuteWdsfSession函数所处理的消息存在缓冲区溢出。在这个请求中有两个字段未经验证长度便拷贝到了固定大小的缓冲区。3在开放注册消息中由于将注册的contact字段未经检查便拷贝到了固定大小的缓冲区，因此可能导致溢出。所有上述溢出漏洞都可能导致以Tivoli服务的权限执行任意指令。', '', '', '2018-04-03 22:34:28.874000');
INSERT INTO "vulnerable" VALUES ('2228', '6217412b-252c-423b-9afa-ef9879c1c248', 'Microsoft Windows SMB Shares Unprivileged Access', 'MicrosoftWindowsSMB共享未授权访问漏洞', '限制基于Windows下或浏览器下的共享权限，合理设置共享权限。', '高危', '通过给出的证书，远程主机拥有一个或多个网络共享，使远程攻击者利用这些共享权限读写保密信息。', '', '', '2018-04-03 22:34:29.002000');
INSERT INTO "vulnerable" VALUES ('2229', 'c5acc736-d7c9-41b0-a6af-87db26e4cc8d', 'MS10-054: Vulnerabilities in SMB Server Could Allow Remote Code Execution (982214) (remote check)', 'SMB服务允许远程代码执行漏洞', '目前厂商已发布WindowsXP,Vista,2008,7,and2008R2平台的相关补丁相关链接：http://technet.microsoft.com/en-us/security/bulletin/MS10-054', '紧急', '远程主机SMB服务允许远程攻击者执行任意代码攻击或拒绝服务攻击。', '', '', '2018-04-03 22:34:29.114000');
INSERT INTO "vulnerable" VALUES ('2230', '3a61820d-ae01-476f-80f1-a0dfa7c47823', 'MS11-020: Vulnerability in SMB Server Could Allow Remote Code Execution (2508429) (remote check)', 'MicrosoftWindowsSMB操作解析远程代码执行漏洞', '目前厂商已发布WindowsXP,Vista,2008,7,and2008R2平台的相关补丁相关链接：http://technet.microsoft.com/en-us/security/bulletin/ms11-020', '紧急', 'MicrosoftWindows是微软发布的非常流行的操作系统。MicrosoftServerMessageBlock(SMB)协议软件处理特制SMB报文时存在未经身份验证的远程代码执行漏洞。利用该漏洞不要求进行身份验证，从而使攻击者通过发送特制的SMB报文到运行Server服务的计算机来利用此漏洞。成功利用此漏洞可完全控制受影响系统。', '', '', '2018-04-03 22:34:29.216000');
INSERT INTO "vulnerable" VALUES ('2231', '0d30ce25-26de-4e5e-89c6-789683c210b6', 'OpenSSL < 0.9.8h Multiple Vulnerabilities', 'OpenSSL版本低于0.9.8h，存在多个安全漏洞', '升级OpenSSL版本至0.9.8l及以上版本，或者使用厂家发布的补丁包进行升级相关链接：http://www.openwall.com/lists/oss-security/2011/12/01/6http://www.openssl.org/news/secadv_20080528.txt', '中危', '主机OpenSSL版本低于0.9.8h,这使得主机存在多个安全漏洞。', '', '', '2018-04-03 22:34:29.323000');
INSERT INTO "vulnerable" VALUES ('2232', 'b0641e3d-1f45-44d2-992c-21526befa155', 'OpenSSL < 0.9.8s Multiple Vulnerabilities', 'OpenSSL版本低于0.9.8s，存在多个安全漏洞', '升级OpenSSL版本至0.9.8s及以上版本，或者使用厂家发布的补丁包进行升级相关链接：http://openssl.org/news/secadv_20120104.txthttp://www.openssl.org/news/changelog.htmlhttp://www.nessus.org/u?c0f10f36http://eprint.iacr.org/2011/232.pdfhttp://cvs.openssl.org/chngview?cn=21301', '高危', '主机OpenSSL版本低于0.9.8s,这使得主机存在多个安全漏洞。', '', '', '2018-04-03 22:34:29.457000');
INSERT INTO "vulnerable" VALUES ('2233', '458ef597-f0b3-46a5-9147-2fa53971c3a0', 'OpenSSL < 0.9.8u Multiple Vulnerabilities', 'OpenSSL版本低于0.9.9s，存在多个安全漏洞', '升级OpenSSL版本至0.9.8u及以上版本，或者使用厂家发布的补丁包进行升级相关链接：http://marc.info/?l=openssl-dev&amp;m=115685408414194&amp;w=2http://openssl.org/news/secadv_20120312.txthttp://www.openssl.org/news/changelog.htmlhttp://www.openwall.com/lists/oss-security/2012/03/13/2http://www.openwall.com/lists/oss-security/2012/02/28/14http://www.nessus.org/u?4a3e3c8ehttp://rt.openssl.org/Ticket/Display.html?id=2711&user=guest&pass=guest', '中危', '主机OpenSSL版本低于0.9.9u,这使得主机存在多个安全漏洞。', '', '', '2018-04-03 22:34:29.563000');
INSERT INTO "vulnerable" VALUES ('2234', '173f016e-3d0a-4537-8ede-513bb04515fa', ' OpenSSL < 0.9.8w ASN.1 asn1_d2i_read_bio Memory Corruption', 'OpenSSL版本低于0.9.8w，ASN.1asn1_d2i_read_bio内存破坏漏洞', '升级OpenSSL版本至0.9.8w及以上版本相关链接：http://openssl.org/news/secadv_20120419.txthttp://seclists.org/fulldisclosure/2012/Apr/210http://openssl.org/news/secadv_20120424.txthttp://cvs.openssl.org/chngview?cn=22479http://www.openssl.org/news/changelog.html', '高危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。（1）OpenSSL0.9.8v之前版本，1.0.0i之前的1.0.0版本和1.0.1a之前的1.0.1版本中的asn1_d2i_read_bio函数中存在漏洞，该漏洞源于未正确解释整数数据。远程攻击者可利用该漏洞借助特制DER数据导致缓冲区溢出攻击，拒绝服务（内存破坏）或造成其他未明影响。此漏洞已被X.509证书或RSA公钥证实。（2）OpenSSL0.9.8v版本中的crypto/buffer/buffer.c中存在多个整数字符串错误漏洞。远程攻击者可利用这些漏洞借助特制DER数据导致缓冲区溢出攻击，造成拒绝服务（内存损坏）或可能有其他未明影响。该漏洞已被X.509证书或RSA公钥证实。', '', '', '2018-04-03 22:34:29.670000');
INSERT INTO "vulnerable" VALUES ('2235', 'b2566a50-71dc-4380-af85-c9d17d39ac5f', 'OpenSSL 0.9.8 < 0.9.8x DTLS CBC Denial of Service', 'OpenSSL版本低于0.9.8x，DTLSCBC拒绝服务攻击漏洞', '升级OpenSSL版本至0.9.8x及以上版本相关链接：http://openssl.org/news/secadv_20120510.txthttp://www.openssl.org/news/changelog.htmlhttp://cvs.openssl.org/chngview?cn=22538https://bugzilla.redhat.com/show_bug.cgi?id=820686', '中危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。OpenSSL0.9.8x之前版本，1.0.0j之前的1.0.0版本以及1.0.1c之前的1.0.1版本中存在整数下溢漏洞，该漏洞源于TLS1.1版本、TLS1.2版本或者DTLS用于CBC加密。远程攻击者可利用该漏洞借助在某些明确IV计算过程中未正确处理的特制TLS数据包导致拒绝服务（缓冲区越界读取），或者产生其他未明影响。', '', '', '2018-04-03 22:34:29.778000');
INSERT INTO "vulnerable" VALUES ('2236', '0ca3b462-db9b-4dec-ac19-c5c9380b2a6a', 'OpenSSL < 0.9.8y Multiple Vulnerabilities', 'OpenSSL版本低于0.9.8y，存在多个安全漏洞', '升级OpenSSL版本至0.9.8y及以上版本，或者使用厂家发布的补丁包进行升级相关链接：http://www.openssl.org/news/secadv_20130204.txt', '中危', '主机OpenSSL版本低于0.9.8y,这使得主机存在多个安全漏洞。', '', '', '2018-04-03 22:34:29.890000');
INSERT INTO "vulnerable" VALUES ('2237', '71959249-8342-400d-8802-85a00105364e', 'OpenSSL Unsupported', 'OpenSSL版本已不再支持', '升级版本至厂商支持的版本相关链接：https://www.openssl.org/news/openssl-old-notes.html', '紧急', '当前主机的OpenSSL版本已不再受厂商支持，这意味着厂商不再为当前版本发布安全补丁包，即可能存在安全漏洞', '', '', '2018-04-03 22:34:30.015000');
INSERT INTO "vulnerable" VALUES ('2238', '5557bb03-39e7-4107-aa87-d85dbf6d2e25', 'Oracle 9iAS _pages Directory Compiled JSP Source Disclosure', 'Oracle9iASOracleJSP泄漏JSP文件信息漏洞', '临时解决方法：如果您不能立刻安装补丁或者升级，建议您采取以下措施以降低威胁：*在边界防火墙或者主机防火墙上限制不可信用户对OracleApachewebserver80/TCP端口的访问。*编辑$ORACLE_HOME$/apache/apache/conf/httpd.conf文件：为了阻止访问globals.jsa文件，增加下列语句：Orderallow,denyDenyfromall为了阻止访问.java文件，增加下列语句：Orderdeny,allowDenyfromall如果JSP文件保存在一个别名目录中(例如不是在&#34;htdocs&#34;的子目录下)，那么您必须增加下列语句：Orderdeny,allowDenyfromall上面的&#34;dirname&#34;是别名目录的目录名。厂商补丁：Oracle已经为此漏洞提供了相应补丁程序，建议您随时关注厂商主页以获取相关补丁：http://metalink.oracle.com', '中危', 'Oracle9iAS(ApplicationServer)的web服务使用的是ApacheWebServer，它提供了多种应用环境，包括SOAP，PL/SQL，XSQL以及JSP。Oracle9iAS的OracleJSP环境中存在一个安全问题，允许远程攻击者获取翻译后的JSP页面的源代码。另外一个问题允许攻击者获取globals.jsa文件的内容。当用户向运行OracleJSP的服务器请求一个JSP页面时，该JSP页面会首先被翻译，然后编译、执行，并将执行结果返回给客户端。在此过程中，三个临时文件会被创建。如果请求的JSP页面为&#34;foo.jsp&#34;，那三个临时文件就是：_foo$__jsp_StaticText.class_foo.class_foo.java它们会被保存在&#34;/_pages&#34;目录下。如果foo.jsp保存在子目录&#34;bar&#34;下，则上述临时文件会保存在&#34;/_pages/_bar&#34;下。由于翻译后的.java文件中包含JSP源代码，而这些文件又都可以直接通过WEB接口访问，攻击者就可能获取一些敏感信息，例如Oracle数据库的用户名和口令。另外，如果JSP应用程序使用globals.jsa文件来保存全局设置，攻击者也可以直接访问该文件并获取其内容，如果其中包含一些敏感信息，也同样可能导致较严重的安全问题。', '', '', '2018-04-03 22:34:30.114000');
INSERT INTO "vulnerable" VALUES ('2239', '2c328d1a-e047-4379-845e-8dda92b26b69', 'Trend Micro ServerProtect TmRpcSrv.dll RPC Request Multiple Overflows', 'TrendMicroServerProtect多个远程缓冲区溢出漏洞', '目前厂商已经发布了升级补丁以修复这个安全问题，补丁下载链接：http://esupport.trendmicro.com/support/viewxml.do?ContentID=EN-1034290', '紧急', 'TrendServerProtect是一款企业级反病毒程序。TrendServerProtect的RPC通信接口实现上存在多个缓冲区溢出漏洞，远程攻击者可以利用这些漏洞控制系统。TrendServerProtect的StCommon.dll和eng50.dll的某些功能函数在处理从RPC接口传入的超长参数时存在堆栈溢出问题，远程攻击者可以利用这些漏洞通过溢出攻击在系统上执行任意指令。<*链接：http://www.tippingpoint.com/security/advisories/TSRT-07-01.htmlhttp://www.tippingpoint.com/security/advisories/TSRT-07-02.html', '', '', '2018-04-03 22:34:30.222000');
INSERT INTO "vulnerable" VALUES ('2240', 'ba857b5b-bfcb-42f6-8479-7fa50e595356', 'Trend Micro ServerProtect AgRpcCln.dll Buffer Overflow', 'WindowsTrendMicroServerProtect''AgRpcCln.dll''缓冲区溢出漏洞', '应用安全补丁3-Build1176及以上版本相关链接：http://www.zerodayinitiative.com/advisories/ZDI-07-025.htmlhttp://archives.neohapsis.com/archives/bugtraq/2007-05/0090.htmlhttp://www.nessus.org/u?6b7dccdd', '紧急', 'WindowsTrendMicroServerProtect安全补丁3Build1176之前的5.58版本的AgRpcCln.dll中存在缓冲区溢出漏洞。远程攻击者可以借助与RPC请求相关的未知向量，执行任意代码。', '', '', '2018-04-03 22:34:30.340000');
INSERT INTO "vulnerable" VALUES ('2241', '4f926954-b1ca-483b-92a7-bb27d4446e5c', ' Trend Micro ServerProtect Multiple Remote Overflows', 'WindowsTrendMicroServerProtect多个缓冲区溢出漏洞', 'TrendMicro已为Windows/NetWare平台ServerProtect发布了相关补丁相关链接：http://www.trendmicro.com/download/product.asp?productid=17', '紧急', 'WindowsTrendMicroServerProtect存在多个缓冲区溢出漏洞，例如RPC调用远程缓冲区溢出漏洞、RPCFN_SYNC_TASK远程整数溢出漏洞、IDevSpotPhpHostBot''order/login.php''PHP远程文件包含漏洞。', '', '', '2018-04-03 22:34:30.441000');
INSERT INTO "vulnerable" VALUES ('2242', 'd0ddf44a-8870-4a82-be77-0be2bc23b49a', 'Trend Micro ServerProtect for Windows (SpntSvc.exe) StRpcSrv.dll Arbitrary Remote Code Execution', 'TrendMicroServerProtect守护进程SpntSvc.exe权限许可和访问控制漏洞', '厂商已发布SecurityPatch5补丁包代替4相关链接:http://www.zerodayinitiative.com/advisories/ZDI-07-077.htmlhttp://archives.neohapsis.com/archives/bugtraq/2007-12/0228.html', '紧急', 'TrendMicroServerProtect守护进程SpntSvc.exe存在未明危险子函数，该子程序来自于DCE/RPC界面的动态库StRpcSrv.dll，远程攻击者可以获得全部文件系统访问权限并执行任意代码。受影响软件为未打SecurityPatch4安全补丁的Windows平台下的TrendMicroServerProtect5.58', '', '', '2018-04-03 22:34:30.572000');
INSERT INTO "vulnerable" VALUES ('2243', '42ea8149-0c11-440a-b036-12b9f0d49561', 'OpenSSL SSL_OP_NETSCAPE_REUSE_CIPHER_CHANGE_BUG Session Resume Ciphersuite Downgrade Issue', 'OpenSSL密码集降级安全漏洞', '升级版本至OpenSSL0.9.8q/1.0.0.c或者联系厂商。相关链接：http://openssl.org/news/secadv_20101202.txt', '中危', 'OpenSSL是一种开放源码的SSL实现，用来实现网络通信的高强度加密，现在被广泛地用于各种网络应用程序中。当SSL_OP_NETSCAPE_REUSE_CIPHER_CHANGE_BUG启用时，OpenSSL0.9.8q之前版本及1.0.0c之前的1.0.x版本不能正确阻止会话缓存中对加密套接字的修改。远程攻击者可借助和嗅探网络通信以发现会话标识符有关的向量强迫意外密码降低保密程度。', '', '', '2018-04-03 22:34:30.672000');
INSERT INTO "vulnerable" VALUES ('2244', '01e07c8d-9918-4bd6-9cb5-b244f6a1315e', ' IBM AIX TCP Large Send Denial of Service Vulnerability', 'IBMAIXTCPStack拒绝服务漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://aix.software.ibm.com/aix/efixes/security/large_send_advisory.asc', '高危', 'IBMAIX(AdvancedInteractiveeXecutive)是IBM开发的一套商业性质UNIX操作系统。IBMAIX多个版本中存在拒绝服务漏洞。攻击者利用该漏洞导致内核死机，拒绝为合法用户提供服务。这些版本包括：IBMAIX5.3版本,6.1版本以及7.1版本中存在该漏洞，其他版本中也可能存在该漏洞。', '', '', '2018-04-03 22:34:30.788000');
INSERT INTO "vulnerable" VALUES ('2245', '1602e922-c0a3-44e3-888e-0fa161d09b6e', 'IBM Lotus Domino ?ReadDesign Request Design Element Disclosure', 'IBMLotusDomino请求设计元素泄露', '管理员可通过设计一个服务重定向文件，这文件可限制进入的带有“ReadDesign”标签的URLs，并转向一个当前错误页面。', '中危', '远程web服务运行攻击者通过发送特殊伪造的HTTP请求包给LotusDomino服务，从而浏览设计元素的XML列表，相关链接：http://[target]/names.nsf/view?ReadDesign', '', '', '2018-04-03 22:34:30.896000');
INSERT INTO "vulnerable" VALUES ('2246', 'cb2eeed0-77ec-4513-8381-18179a3ea1dd', 'IBM WebSphere Application Server 6.1 < Fix Pack 21 Multiple Flaws', 'IBMWebSphere应用服务版本低于FixPack21，存在多个溢出漏洞', '如果使用WebSphere应用服务,应用修复包FixPack21(6.1.0.21)及以上版本，如果使用嵌入式WebSphere应用服务包，则推荐应用最新的eWASfixpack修复包。相关链接：http://www-01.ibm.com/support/docview.wss?uid=swg21404665http://www-01.ibm.com/support/docview.wss?uid=swg27009778http://www-01.ibm.com/support/docview.wss?&uid=swg1PK63182http://www-01.ibm.com/support/docview.wss?uid=swg27007951#61021', '中危', 'IBMWebSphere应用服务版本低于FixPack21，存在多个溢出漏洞，PMI已启用，本地攻击者可通过''Systemout.log''and''ffdc''文件获取敏感信息；SSL当前设置属性''SecurityLevel''不能正确执行当前的加密级别', '', '', '2018-04-03 22:34:31.009000');
