INSERT INTO "vulnerable" VALUES ('2296', 'ced2ff2b-6d19-4631-be14-2b371f1182c1', 'MS10-012: Vulnerabilities in SMB Could Allow Remote Code Execution (971468) (uncredentialed check)', 'MS10-012：SMB远程代码执行漏洞', '升级补丁ms10-012http://technet.microsoft.com/zh-CN/security/bulletin/ms10-012', '高危', 'MicrosoftSMB协议软件没有正确的处理在协商期间解析SMB报文时可能出现的竞争条件，未经认证的攻击者可以通过向运行Server服务的计算机发送特制网络消息来利用该漏洞，导致受影响系统停止响应，直至手动重新启动', '', '', '2018-04-03 22:34:37.175000');
INSERT INTO "vulnerable" VALUES ('2297', '6feb5062-3a86-4308-b3ea-68005e3090b5', 'Microsoft Windows shlwapi.dll Malformed HTML Tag Handling Null Pointer DoS', 'Windowsshlwapi.dll拒绝服务漏洞', '更新到最新版本的shlwapi.dll文件', '中危', '旧版本的shlwapi.dll文件在处理HTMLform时，存在拒绝服务攻击漏洞。', '', '', '2018-04-03 22:34:37.299000');
INSERT INTO "vulnerable" VALUES ('2298', 'a3e90e16-0be8-4743-ab3e-0410a3a04f66', 'Microsoft Windows 2000 SP4 Update Rollup 1 Missing', 'Micorsoftwindows2000Sp4累积更新包安全模板设置漏洞', '建议升级操作系统到厂商所支持的版本', '高危', 'MicrosoftWindows2000SP4UpdateRollup1之前版本的SECEDIT命令在使用安全模板设置文件夹的访问控制列表(ACL)时，没有在长文件夹条目后列出的文件夹上应用ACL，会导致安全许可低于模板的指定。', '', '', '2018-04-03 22:34:37.419000');
INSERT INTO "vulnerable" VALUES ('2299', '31503193-afec-4064-addb-404ee0e3d1e0', 'Microsoft Windows SMB Blank Administrator Password', 'WindowsSMB管理员账号空口令', '设置符合一定强度的口令', '紧急', '默认情况下administrator管理员账号为空口令', '', '', '2018-04-03 22:34:37.544000');
INSERT INTO "vulnerable" VALUES ('2300', '4360eb9d-4d29-4bd1-98a1-aa1b522a6a05', 'MS KB953839: Cumulative Security Update of ActiveX Kill Bits', 'MSKB953839:缺少ActiveXKillBits安全更新', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://technet.microsoft.com/en-us/security/advisory/953839', '中危', '未安装killbitsforActiveXcontrols的安全补丁MSKB953839', '', '', '2018-04-03 22:34:37.654000');
