INSERT INTO "vulnerable" VALUES ('2302', 'a16bcca7-88dc-4243-b450-788ad594e836', 'MS KB960715: Cumulative Security Update of ActiveX Kill Bits', 'MSKB960715:缺少ActiveXKillBits安全更新', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://technet.microsoft.com/en-us/security/advisory/960715', '中危', '未安装killbitsforActiveXcontrols的安全补丁MSKB960715', '', '', '2018-04-03 22:34:37.928000');
