INSERT INTO "vulnerable" VALUES ('2304', 'd98f6e97-5cd5-4427-840c-a643eef60290', 'MS KB955759: Security Enhancements for the Indeo Codec', 'MSKB955759:缺少IndeoCodec安全更新', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://support.microsoft.com/kb/KB955759', '高危', 'MicrosoftWindows2000SP4,XPSP2和SP3,以及Server2003SP2中的Indeo32codec中的ir32_32.dll3.24.15.3，使远程攻击者可以借助一个多媒体文件中的畸形数据引起一个拒绝服务(堆崩溃)或执行任意代码。例如一个AVI文件。', '', '', '2018-04-03 22:34:38.123000');
INSERT INTO "vulnerable" VALUES ('2305', 'aca32224-4dd5-44b3-83b1-9c86d3e04df5', 'MS KB2269637: Insecure Library Loading Could Allow Remote Code Execution', 'MSKB2269637:不安全的库文件加载远程代码执行漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://support.microsoft.com/kb/2264107', '高危', '应用程序解析自己的当前工作目录的DLL文件实现方式不安全，如果一个恶意DLL与所需的DLL同名，且位于应用程序的当前工作目录，那么该恶意DLL将被加载。攻击者可利用该漏洞来远程执行恶意代码。', '', '', '2018-04-03 22:34:38.227000');
INSERT INTO "vulnerable" VALUES ('2306', '33044ec8-064a-43b4-94f9-bb38c9fe6f69', 'Microsoft XML Parser (MSXML) and XML Core Services Unsupported', '使用不安全的XML解析器', '更新使用安全的MicrosoftXMLParser(MSXML)或XMLCoreServiceshttp://support.microsoft.com/kb/269238', '高危', '当前所使用的MicrosoftXMLParser(MSXML)或XMLCoreServices的版本，厂商已不再提供支持。意味着将不会有新的安全补丁，所以存在较大的安全风险隐患。', '', '', '2018-04-03 22:34:38.413000');
INSERT INTO "vulnerable" VALUES ('2307', 'b8848d10-567f-4593-9439-82ac68583e7d', 'Microsoft Windows Unquoted Service Path Enumeration', 'MicrosoftWindows未闭合服务路径枚举漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://www.symantec.com/security_response/securityupdates/detail.jsp?fid=security_advisory&pvid=security_advisory&year=&suid=20130321_00目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://www.schneider-electric.com/site/home/index.cfm/ww/', '高危', '当服务的可执行路径包含空字符时，本地攻击者可借助恶意的程序利用该漏洞获取特权。', '', '', '2018-04-03 22:34:38.594000');
INSERT INTO "vulnerable" VALUES ('2308', 'cf535cdf-c0a3-4591-a49f-c2cc3cab24e9', 'MS05-046: Vulnerability in the Client Service for NetWare Could Allow Remote Code Execution (899589) (uncredentialed check)', 'MS05-046：WindowsNetwareCSNW网络消息处理远程溢出漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://technet.microsoft.com/en-us/security/bulletin/ms05-046', '紧急', 'MicrosoftWindows2000SP4，XPSP1andSp2，Server2003SP1以及早些版本中的CSNW允许攻击者发送特制的网络消息，执行任意代码。MicrosoftWindowsNetware客户端服务中存在缓冲区溢出漏洞。攻击者可以通过网络向受影响的系统发送特制的网络消息，导致执行任意代码。请注意默认下任何操作系统版本都没有安装CSNW。', '', '', '2018-04-03 22:34:38.752000');
INSERT INTO "vulnerable" VALUES ('2309', '5180cd29-ebc7-4f23-84b2-9344e2fb650b', 'Unencrypted Telnet Server', '未加密的Telnet服务', '禁用Telnet服务，并使用SSH代替。', '中危', '远程主机正运行着Telnet服务，此项服务明文传输，使用未加密的Telnet服务，登录名、密码及命令均采取明文传输，这使得中间人攻击者截取Telnet会话，获取登录凭证或其它敏感信息，并在服务器和客户端之间篡改报文。', '', '', '2018-04-03 22:34:38.858000');
INSERT INTO "vulnerable" VALUES ('2310', 'ffb9425b-0514-4635-ac0d-679d1a50c678', 'ESXi 5.0 < Build 1749766 Multiple Vulnerabilities (remote check)', '远程VMwareESXi主机存在多种类型漏洞', '安装补丁包ESXi500-201405001，相关链接：http://www.vmware.com/security/advisories/VMSA-2014-0002.htmlhttps://www.vmware.com/security/advisories/VMSA-2015-0001.htmlhttp://kb.vmware.com/kb/2075521', '中危', '远程主机ESXi版本过低，可能存在多个安全漏洞。', '', '', '2018-04-03 22:34:38.988000');
INSERT INTO "vulnerable" VALUES ('2311', '4e1f5c3d-08fd-4a11-b909-845f737b6dc8', 'SSL RC4 Cipher Suites Supported', '远程主机支持SSLRC4加密套件', '重新设置受影响的应用程序，可能的话，避免使用RC4密钥算法，可以考虑给浏览器或WEB服务支持的带有AES-GCM的TLS1.2，相关链接：http://www.nessus.org/u?217a3666http://cr.yp.to/talks/2013.03.12/slides.pdfhttp://www.isg.rhul.ac.uk/tls/http://www.imperva.com/docs/HII_Attacking_SSL_when_using_RC4.pdf', '中危', '远程主机在一个或多个支持使用RC4密码套件，RC4密码是有缺陷的生成伪随机流的字节数,如果明文重复加密(如HTTPcookie),攻击者能够获得许多(即数以百万计)明文密文配对,攻击者可以获得密文后，对比获得明文。', '', '', '2018-04-03 22:34:39.100000');
INSERT INTO "vulnerable" VALUES ('2312', '54e83c7f-e1e9-46c4-b67a-06ee0d9e812a', 'SSL/TLS Diffie-Hellman Modulus <= 1024 Bits (LogJam)', 'SSL/TLSDiffie-Hellman密钥算法位数小于或等于1024位', '重新设置此项服务，或使用Diffie-Hellman密码算法位数大于或等于2048位，相关链接：http://weakdh.org/', '中危', '远程主机允许SSL/TLS连接的一个或多个Diffie-Hellman密钥算法位数小于或等于1024位。通过密码分析,第三方可以在很短的时间内找到共享密钥(根据算法位数大小和攻击者参考资料)。这可能允许攻击者恢复到明文或破坏连接的完整性。', '', '', '2018-04-03 22:34:39.197000');
INSERT INTO "vulnerable" VALUES ('2313', 'd5babc39-9104-4d77-a694-8dd78eace033', 'SSL/TLS EXPORT_RSA <= 512-bit Cipher Suites Supported (FREAK)', 'SSL/TLSEXPORT_RSA密钥算法位数小于或等于512位', '重新设置服务，或移除支持EXPORT_RSA的密钥层。相关链接：https://www.smacktls.com/#freakhttps://www.openssl.org/news/secadv_20150108.txthttp://www.nessus.org/u?b78da2c4', '中危', '远程主机运行着弱密钥算法，攻击者可以在短时间内破解密文。', '', '', '2018-04-03 22:34:39.306000');
INSERT INTO "vulnerable" VALUES ('2314', '7efd99b0-ebe3-40ce-ad54-5e75a5f390b6', 'SSL/TLS EXPORT_DHE <= 512-bit Export Cipher Suites Supported (Logjam)', 'SSL/TLSEXPORT_DHE密钥算法位数小于或等于512位', '重新设置服务，或移除支持EXPORT_DHE的密钥层。相关链接：https://weakdh.org/', '中危', '远程主机运行的EXPORT_DHE密钥算法小于或等于512位，通过密文分析，第三方攻击者可以在短时间内获取共享密钥。', '', '', '2018-04-03 22:34:39.419000');
INSERT INTO "vulnerable" VALUES ('2315', 'f9c4ad06-3b81-4739-a510-df2b2bc34f58', 'Apache Tomcat 6.0.x < 6.0.43 Multiple Vulnerabilities (POODLE)', 'ApacheTomcat版本低于6.0.43，存在多个安全漏洞', '升级ApacheTomcat版本至6.0.43及以上。相关链接：http://tomcat.apache.org/tomcat-6.0-doc/changelog.htmlhttps://www.imperialviolet.org/2014/10/14/poodle.htmlhttps://www.openssl.org/~bodo/ssl-poodle.pdfhttps://tools.ietf.org/html/draft-ietf-tls-downgrade-scsv-00', '高危', 'ApacheTomcat版本低于6.0.43，存在多个安全漏洞。', '', '', '2018-04-03 22:34:39.551000');
INSERT INTO "vulnerable" VALUES ('2316', '5420d276-df3b-4dc7-b039-9ed850f6e108', ' Default Password for FTP ''admin'' Account', 'FTP默认账户admin存在默认密码', '更换admin账户默认密码。相关链接：http://archives.neohapsis.com/archives/bugtraq/2003-04/0202.html', '紧急', '远程的FTP服务的admin账户存在默认密码password，攻击者可远程获得主机权限，发起更深一层的攻击。如果远程主机是NB1300系列路由器，这可能运行攻击者偷到WAN证书，甚至重新设置路由信息。', '', '', '2018-04-03 22:34:39.663000');
INSERT INTO "vulnerable" VALUES ('2317', '5e56a34e-302e-4a47-a95c-31e588df2ae0', 'MySQL Protocol Remote User Enumeration', 'MySQL协议远程用户枚举', '暂时还未有解决方案，相关链接：http://archives.neohapsis.com/archives/fulldisclosure/2012-12/0010.htmlhttps://mariadb.atlassian.net/browse/MDEV-3909', '中危', '远程数据库服务器存在信息泄露漏洞，远程主机运行的MySQL或MariaDB版本存在用户枚举漏洞，远程的非认证的攻击者可利用这个获取有效数据库用户的名字，这些信息可允许攻击者发起更深一层攻击。', '', '', '2018-04-03 22:34:39.773000');
INSERT INTO "vulnerable" VALUES ('2318', '1ecd8273-5e82-45aa-a3d3-c36a2e8e54e3', 'Apache 2.4 < 2.4.12 Multiple Vulnerabilities', 'Apache版本低于2.4.12，存在多个安全漏洞', '确保受影响的模块没有被使用，或者升级Apache版本至2.4.12及以上。相关链接：http://www.apache.org/dist/httpd/CHANGES_2.4.12http://httpd.apache.org/security/vulnerabilities_24.html', '中危', 'Apache版本低于2.4.12，存在多个安全漏洞。', '', '', '2018-04-03 22:34:39.883000');
INSERT INTO "vulnerable" VALUES ('2319', '391bced1-fbfe-4710-b092-55326e321643', 'OpenSSL 0.9.8 < 0.9.8zd Multiple Vulnerabilities', 'OpenSSL版本低于0.9.8zd，存在多个安全漏洞', '升级OpenSSL版本至0.9.8zd及以上。相关链接：https://www.openssl.org/news/openssl-0.9.8-notes.htmlhttps://www.openssl.org/news/secadv_20150108.txthttps://www.openssl.org/news/vulnerabilities.htmlhttps://www.smacktls.com/#freak', '中危', 'OpenSSL版本低于0.9.8zd，存在多个安全漏洞。', '', '', '2018-04-03 22:34:39.998000');
