INSERT INTO "vulnerable" VALUES ('2327', 'd3d1e603-4685-4ad9-8b3c-dacb4fd6ede3', 'OpenSSL 1.0.1 < 1.0.1k Multiple Vulnerabilities', 'OpenSSL版本低于1.0.1k，存在多个安全漏洞', '升级OpenSSL版本至1.0.1k及以上。相关链接：https://www.openssl.org/news/openssl-1.0.1-notes.htmlhttps://www.openssl.org/news/secadv_20150108.txthttps://www.openssl.org/news/vulnerabilities.htmlhttps://www.smacktls.com/#freak', '中危', 'OpenSSL版本低于1.0.1k，存在多个安全漏洞', '', '', '2018-04-03 22:35:41.581000');
