INSERT INTO "vulnerable" VALUES ('2394', 'c4d73117-a820-42f3-bf42-7ff1d8d6ef25', 'PHP 5.5.x < 5.5.9 GD Extension Multiple Vulnerabilities', 'PHP版本低于5.5.9', '升级PHP版本至5.5.9及以上版本。', '高危', 'PHP5.5.8及之前的版本中的ext/gd/gd.c文件中的‘gdImageCrop’函数中存在整数溢出漏洞。远程攻击者可通过调用‘imagecrop’函数利用该漏洞造成拒绝服务（应用程序崩溃），也可能执行任意代码。', '', '', '2018-04-03 22:35:49.716000');
