INSERT INTO "vulnerable" VALUES ('2549', '19e61e84-1f0e-42cf-a931-04758dce2f8f', 'Ubuntu 12.04 LTS / 14.04 / 15.04 : net-snmp vulnerabilities (USN-2711-1)', 'Net-SNMPsnmptrapd资源管理错误漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://net-snmp.sourceforge.net/download.html', '高危', 'Net-SNMP是一套开源的简单网络管理协议（SimpleNetworkManagementProtocol）软件。该软件用于监控网络设备、电脑设备、UPS设备等。Net-SNMP5.7.0及之前版本中的snmplib/mib.c文件中存在安全漏洞。远程攻击者可借助特制的SNMPtrap消息利用该漏洞造成拒绝服务（snmptrapd崩溃）。', '', '', '2018-04-03 22:36:08.262000');
