INSERT INTO "vulnerable" VALUES ('2774', '2bd1bace-ef38-4583-8374-b4fe02bee47e', 'MS05-045: Vulnerability in Network Connection Manager Could Allow Denial of Service (905414)', 'Microsoftwindows网络连接管理器netman.dll拒绝服务漏洞', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：http://www.microsoft.com/technet/security/Bulletin/MS05-045.mspx', '中危', '网络连接管理器服务的netman.dll中存在拒绝服务漏洞。攻击者可以通过向特定函数发送特制的整数参数，导致网络连接服务崩溃。', '', '', '2018-04-03 22:36:35.064000');
