INSERT INTO "vulnerable" VALUES ('2779', 'bae3bb50-3187-4945-891b-71468c8beef5', 'MS05-051: Vulnerabilities in MSDTC and COM+ Could Allow Remote Code Execution (902400)', 'MicrosoftWindowsMSDTC内存破坏漏洞(MS05-051)', '目前厂商已经发布了升级补丁以修复此安全问题，补丁获取链接：https://technet.microsoft.com/library/security/ms05-051', '紧急', 'MicrosoftWindowsMSDTC处理用户请求存在漏洞，允许远程攻击者在任意内存地址写入有限有限的数据字节，成功利用此漏洞可以在一些Windows系统上执行任意指令，从而完全控制系统。', '', '', '2018-04-03 22:36:35.738000');
