INSERT INTO "vulnerable" VALUES ('3064', '951cbef2-0b2b-4a7f-9c40-c1fb14259baf', 'ESXi 6.0 < Build 3380124 Shared Folders (HGFS) Guest Privilege Escalation (VMSA-2016-0001) (remote check)', 'ESXi6.0<Build3380124共享文件夹（HGFS）访客权限升级', '相关补丁链接请参照：http://www.vmware.com/security/advisories/VMSA-2016-0001.html', '中危', 'VMwareESX/ESXi主机缺少安全相关安全补丁，攻击者可利用该漏洞获取系统权限或发起拒绝服务攻击', '', '', '2018-04-03 22:37:09.934000');
