INSERT INTO "vulnerable" VALUES ('3068', '6361b210-1be2-4c1c-82dc-4fd6cc738c56', 'VMware vCenter Server 6.0.x <6.0u3b / 6.5.x <6.5c BlazeDS AMF3 RCE（VMSA-2017-0007）', 'VMwarevCenterServer6.0.x<6.0u3b/6.5.x<6.5cBlazeDSAMF3RCE（VMSA-2017-0007）', '升级到VMwarevCenterServer版本6.0u3b（6.0.0build-5326177）/6.0（6.0.0build-5318198）/6.5.0c（6.5.0build-5318112）或更高版本上的u3b。或者，应用供应商提供的解决方法。', '中危', '主机上的虚拟化管理应用程序受到远程代码执行漏洞的影响', '', '', '2018-04-03 22:37:10.425000');
